// script.js

// Initialize Leaflet map
const map = L.map('mapContainer').setView([24.0875, 32.8917], 13);

// إضافة خريطة الأساس
L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
  attribution: 'Map data © <a href="https://openstreetmap.org">OpenStreetMap</a> contributors'
}).addTo(map);

// تعريف المتغيرات للطبقات
let salugaLayer, phenomenaLayer, plantsLayer;

// كائن لتجميع الطبقات
const overlays = {};

// تحميل طبقة حدود المحمية
fetch('map.geojson')
  .then(res => res.json())
  .then(data => {
    salugaLayer = L.geoJSON(data, {
      style: {
        color: "brown",
        weight: 2,
        fillColor: "#DEB887",
        fillOpacity: 0.3
      },
      onEachFeature: (feature, layer) => {
        if (feature.properties?.name) {
          layer.bindPopup(` ${feature.properties.name}`);
        }
      }
    }).addTo(map);
    overlays["حدود المحميه"] = salugaLayer;
    updateLayerControl(); // تحديث الكنترول بعد إضافة الطبقة
  });
// تحميل طبقة النباتات
fetch('plants.geojson')
  .then(res => res.json())
  .then(data => {
    plantsLayer = L.geoJSON(data, {
      pointToLayer: (feature, latlng) => L.circleMarker(latlng, {
        radius: 10,
        fillColor: "green",
        color: "black",
        weight: 1,
        opacity: 1,
        fillOpacity: 0.5
      }),
      onEachFeature: (feature, layer) => {
        if (feature.properties?.name) {
          layer.bindPopup(` ${feature.properties.name}`);
        }
      }
    }).addTo(map);
    overlays["النباتات"] = plantsLayer;
    updateLayerControl();
  });
// تحميل طبقة الظواهر
fetch('phenomena.geojson')
  .then(res => res.json())
  .then(data => {
    phenomenaLayer = L.geoJSON(data, {
      pointToLayer: (feature, latlng) => L.circleMarker(latlng, {
        radius: 6,
        fillColor: "orange",
        color: "black",
        weight: 1,
        opacity: 1,
        fillOpacity: 0.6
      }),
      onEachFeature: (feature, layer) => {
        if (feature.properties?.name) {
          layer.bindPopup(` ${feature.properties.name}`);
        }
      }
    }).addTo(map);
    overlays["الظواهر الطبيعيه"] = phenomenaLayer;
    updateLayerControl();
  });
// تحميل طبقة المسارات
fetch('tracks.geojson')
  .then(res => res.json())
  .then(data => {
    pathsLayer = L.geoJSON(data, {
      style: {
        color: 'blue',       // لون الخط
        weight: 3,           // سماكة الخط
        opacity: 0.8         // شفافية الخط
      },
      onEachFeature: (feature, layer) => {
        if (feature.properties?.name) {
          layer.bindPopup(`${feature.properties.name}`);
        }
      }
    }).addTo(map);

    overlays["الطرق داخل المحميه"] = pathsLayer;
    updateLayerControl();
  });

// تعريف الكنترول مرة واحدة فقط
let layersControl;

function updateLayerControl() {
  if (layersControl) {
    map.removeControl(layersControl); // إزالة الكنترول القديم لو موجود
  }
  layersControl = L.control.layers({}, overlays).addTo(map);
}



